import React, {Component} from "react";
import "./App.css";
import Jay from "./bluejay.png";
import {BrowserRouter as Router, Switch, Route, Link} from "react-router-dom";

class BD extends Component {
    constructor(prop) {
        super(prop);
        this.state = {
            schedule1: [],
            schedule2: [],
            schedule3: [],
            schedule4: [],
            schedule5: [],
            schedule6: [],
            schedule7: [],
            schedule8: []
        };
    }

    callAPI() {
        fetch("http://localhost:5000/api/bd/courses")
            .then(res => res.json())
            .then(res => this.setState({
                schedule1: res.slice(0, 3),
                schedule2: res.slice(3, 6),
                schedule3: res.slice(6, 9),
                schedule4: res.slice(9, 12),
                schedule5: res.slice(12, 15),
                schedule6: res.slice(15, 18),
                schedule7: res.slice(18, 21),
                schedule8: res.slice(21, 24),
            }))
            .catch(err => err);
    }

    componentDidMount() {
        this.callAPI();
    }

    render() {
        const list1 = this.state.schedule1.map(d => {
            return (
                <li>
                    {d.CourseNumber}
                    {": "}
                    {d.CourseTitle}
