import React, { Component } from "react";
import logo from "./logo.svg";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    return (
      <Router>
        <Switch>
          <Route exact path="/">
            <CoursesTaken />
          </Route>
          <Route exact path="/focus_area">
            <FocusArea />
          </Route>
          <Route exact path="/nlp">
            <NLP />
          </Route>
          <Route exact path="/r">
            <ROB />
          </Route>
          <Route exact path="/bd">
            <BD />
          </Route>
          <Route exact path="/is">
            <IS />
          </Route>
          <Route exact path="/cb">
            <CB />
          </Route>
        </Switch>
      </Router>
    );
  }
}


class CoursesTaken extends Component {
  constructor(prop) {
    super(prop);
    this.state = {
      question: "What courses have you taken?",
      value: "", // for display in the input box
      allCourses: [], 
      myCourses: ["helo", "jdahsj"]
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  callAPI() {
    console.log("fetching from api");
    fetch("http://localhost:5000/api/nlp/courses") // to be changed
      .then(res => res.json())
      .then(res => this.setState({ allCourses: res }))
      .catch(err => err);
  }

  componentDidMount() {
    this.callAPI();
  }

  handleChange(event) {
    this.setState({value: event.target.value}); // prints input out by setting values to the changes
  }

  handleSubmit(event) {
    alert('Successfully added ' + this.state.value + '!');
    this.state.myCourses.push(this.state.value);
    event.preventDefault();
  }

  render() {
      return (
        <div>
          <h1
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}
          >
            {this.state.question}
          </h1>
          <form onSubmit={this.handleSubmit} style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}>
            <input type="text" value={this.state.value} size="60" onChange={this.handleChange}/>
            <Link to="/focus_area">
              <button class="button0" type="button" align="right">
                That's it
              </button>
          </Link>
          </form>
          <div>{this.state.myCourses}</div>

        </div>
    );
  }
}


class Dropdown extends Component {
  constructor(prop) {
    super(prop);
    this.state = {
      question: "What is your focus area in computer science?",
      schedule: []
    };
  }

  callAPI() {
    console.log("fetching from api");
    fetch("http://localhost:5000/api/nlp/courses") //to be changed
      .then(res => res.json())
      .then(res => this.setState({ schedule: res }))
      .catch(err => err);
  }

  componentDidMount() {
    this.callAPI();
  }


}

class FocusArea extends Component {
  constructor(prop) {
    super(prop);
