  }

  render() {
    return (
      <div>
        <h1
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center"
          }}
        >
          {this.state.question}
        </h1>
        <div
          style={{
            marginLeft: "auto",
            display: "flex",
            justifyContent: "center"
          }}
        >
        <Link to="/r">
          <button class="button1" type="button">
            Robotics
          </button>
        </Link>
        <Link to="/bd">
          <button class="button4" type="button">
            Big Data
          </button>
        </Link>
        <Link to="/is">
          <button class="button5" type="button">
            Information Security
          </button>
        </Link>
        <Link to="/cb">
          <button class="button3" type="button">
            Computational Biology
          </button>
        </Link>
        <Link to="/nlp">
          <button class="button2" type="button">
            Natural Language Processing
          </button>
        </Link>
        </div>
      </div>
    );
  }
}


class NLP extends Component {
  constructor(prop) {
    super(prop);
    this.state = {
      apiResponse: "",
      schedule: []
    };
  }

  callAPI() {
    console.log("fetching from api");
    fetch("http://localhost:5000/api/nlp/courses")
      .then(res => res.json())
      .then(res => this.setState({ schedule: res }))
      .catch(err => err);
  }

  componentDidMount() {
    this.callAPI();
  }

  render() {
    const listItems = this.state.schedule.map(d => {
      return (
        <li>
          {d.CourseNumber}
          {": "}
          {d.CourseTitle}
          {" Credit:"}
          {d.Credits}
          {" Instructor:"}
          {d.Instructor}
        </li>
      );
    });
    return (
      <div>
        <h1
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center"
          }}
        >
          Welcome to Natural Language Processing!
        </h1>
        <div>{listItems}</div>
      </div>
    );
  }
}

