  }

  componentDidMount() {
    this.callAPI();
  }

  render() {
    const listItems = this.state.schedule.map(d => {
      return (
        <li>
          {d.CourseNumber}
          {": "}
          {d.CourseTitle}
          {" Credit:"}
          {d.Credits}
          {" Instructor:"}
          {d.Instructor}
        </li>
      );
    });
    return (
      <div>
        <h1
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center"
          }}
        >
          Welcome to Big Data!
        </h1>
        <div>{listItems}</div>
      </div>
    );
  }
}

export default App;
