import React, { Component } from "react";
import logo from "./logo.svg";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import "./App.css";
import Autosuggest from 'react-autosuggest';




class App extends Component {
    constructor(props) {
      super(props);
      this.state = {};
    }

    render() {
      return (
        <Router>
          <Switch>
            <Route exact path="/">
              <CoursesTaken />
            </Route>
            <Route exact path="/focus_area">
              <FocusArea />
            </Route>
            <Route exact path="/nlp">
              <NLP />
            </Route>
            <Route exact path="/r">
              <ROB />
            </Route>
            <Route exact path="/bd">
              <BD />
            </Route>
            <Route exact path="/is">
              <IS />
            </Route>
            <Route exact path="/cb">
              <CB />
            </Route>
          </Switch>
        </Router>
      );
    }
  }



class CoursesTaken extends Component {
  constructor(prop) {
    super(prop);
    this.state = {
      question: "What courses have you taken?",
      value: '',
      suggestions: [],
      allCourses: ["Hellen", "hello", "bye"],
      myCourses: []
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  // callAPI() {
  //   console.log("fetching from api");
  //   fetch("http://localhost:5000/api/courses") // to be changed
  //     .then(res => res.json())
  //     .then(res => this.setState({ allCourses: res }))
  //     .catch(err => err);
  // }

  sendAPI(data) {
    console.log("posting to api");
    fetch('http://localhost:5000/api/user_info', {
      method: 'POST',
      body: JSON.stringify(data)
    }).then(res => res.json())
    .then(data => console.log("Success", data))
    .catch(err => console.log("Error:", err));
  }

  // componentDidMount() {
  //   this.callAPI();
  // }

  // Teach Autosuggest how to calculate suggestions for any given input value.
  getSuggestions = value => {
    const inputValue = value.trim().toLowerCase();
    const inputLength = inputValue.length;

    return inputLength === 0 ? [] : this.state.allCourses.filter(ac =>
      ac.toLowerCase().slice(0, inputLength) === inputValue
    );
  };

  // When suggestion is clicked, Autosuggest needs to populate the input
  // based on the clicked suggestion. Teach Autosuggest how to calculate the
  // input value for every given suggestion.
  getSuggestionValue = suggestion => suggestion;

  // Use your imagination to render suggestions.
  renderSuggestion = suggestion => (
    <div>
      {suggestion}
    </div>
  );

  onChange = (event, { newValue }) => {
    this.setState({
      value: newValue
    });
  };

  // Autosuggest will call this function every time you need to update suggestions.
    // You already implemented this logic above, so just use it.
    onSuggestionsFetchRequested = ({ value }) => {
      this.setState({
        suggestions: this.getSuggestions(value)
      });
    };

    // Autosuggest will call this function every time you need to clear suggestions.
    onSuggestionsClearRequested = () => {
      this.setState({
        suggestions: []
      });
    };

  handleSubmit(event) {
    alert('Successfully added ' + this.state.value + '!');
    this.setState(state => {
      const myCourses = state.myCourses.concat(state.value);
      return {
        value:"",
        myCourses,
      };
    });
    event.preventDefault();
  }


  render() {
      // const optionList = this.state.allCourses.map(it => 
      //         <option>
      //           {it.CourseTitle}
      //         </option>
      //         );
      //   console.log("OPTION:",optionList)

      const { value, suggestions } = this.state;

      // Autosuggest will pass through all these props to the input.
      const inputProps = {
        placeholder: 'Type a programming language',
        value,
        onChange: this.onChange
      };

      return (
        <div class="center">
          <h1
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}
          >
            {this.state.question}
          </h1>
          {/* <form onSubmit={this.handleSubmit} style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}>
            <label style={{color:"blue"}}> <i class="iconfont">&#xe600;</i>Choose your course from the list below
            <div>
            <select id="courses" value={this.state.value} onChange = {this.handleChange}>
            <option value="">Select Your Courses </option>
  {optionList}
</select> 
</div>
<input class = "button_t" type="submit" value="I've taken it!" />
