          <div class="text1">
            <h1>Semester 2</h1>
          </div>
          <div class="box1">{list2}</div>
          </div>
          <div class="text3">
            <h1>Semester 3</h1>
          </div>
          <div class="box3">{list3}</div>
          <div class="text3">
            <h1>Semester 4</h1>
          </div>
          <div class="box3">{list3}</div>
          <div class="text3">
            <h1>Semester 5</h1>
          </div>
          <div class="box3">{list3}</div>
          <div class="text3">
            <h1>Semester 6</h1>
          </div>
          <div class="box3">{list3}</div>
          <div class="text3">
            <h1>Semester 7</h1>
