import React, { Component } from "react";
import logo from "./logo.svg";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    return (
      <Router>
        <Switch>
          <Route exact path="/">
            <CoursesTaken />
          </Route>
          <Route exact path="/focus_area">
            <FocusArea />
          </Route>
          <Route exact path="/nlp">
            <NLP />
          </Route>
          <Route exact path="/r">
            <ROB />
          </Route>
          <Route exact path="/bd">
            <BD />
          </Route>
          <Route exact path="/is">
            <IS />
          </Route>
          <Route exact path="/cb">
            <CB />
          </Route>
        </Switch>
      </Router>
    );
  }
}


class CoursesTaken extends Component {
  constructor(prop) {
    super(prop);
    this.state = {
      question: "What courses have you taken?",
      value: "", // for display in the input box
      allCourses: [], 
      myCourses: []
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  callAPI() {
    console.log("fetching from api");
    fetch("http://localhost:5000/api/courses") // to be changed
      .then(res => res.json())
      .then(res => this.setState({ allCourses: res }))
      .catch(err => err);
  }

  componentDidMount() {
    this.callAPI();
  }

  handleChange(event) {
    this.setState({value: event.target.value}); // prints input out by setting values to the changes
  }

  handleSubmit(event) {
    alert('Successfully added ' + this.state.value + '!');
    this.setState(state => {
      const myCourses = state.myCourses.concat(state.value);
      return {
        value:"",
        myCourses,
      };
    });
    event.preventDefault();
  }

  render() {
    const courseLists = this.state.allCourses.map(d => {
      return (
        <li>
          {d.CourseNumber}
          {": "}
          {d.CourseTitle}
          {" Credit:"}
          {d.Credits}
          {" Instructor:"}
          {d.Instructor}
        </li>
      );
    });
      return (
        <div class="center">
          <h1
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}
          >
            {this.state.question}
          </h1>
          <form onSubmit={this.handleSubmit} style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}>
            <input type="text" value={this.state.value} size="60" onChange={this.handleChange}/>
            <Link to="/focus_area">
              <button class="button0" type="button" align="right">
                That's it
              </button>
          </Link>
          </form>

           <div>
          <label>
    Choose a course from this list:
    <input list="courses" name="myBrowser" autoComplete = "on"/>  
</label>   
<datalist id="courses">
      {/* <option value="Chrome" />
    <option value="Firefox" />
    <option value="Internet Explorer" />
    <option value="Opera" />
    <option value="Safari" />
    <option value="Microsoft Edge" />       */}
       {courseLists.map(item =>
      <option value = {item.CourseTitle} />
    )}  
</datalist>

          </div> 

          <div>{this.state.myCourses.map(data => (<li>{data}</li>))}</div>



        {/*<div>
          {this.state.allCourses}
        </div> */} 

        <div>
          {courseLists}
        </div>

        </div>
    );
  }
}


class Dropdown extends Component {
  constructor(prop) {
    super(prop);
    this.state = {
      question: "What is your focus area in computer science?",
      schedule: []
    };
  }

  callAPI() {
    console.log("fetching from api");
    fetch("http://localhost:5000/api/nlp/courses") //to be changed
      .then(res => res.json())
      .then(res => this.setState({ schedule: res }))
      .catch(err => err);
  }

  componentDidMount() {
    this.callAPI();
  }


}

class FocusArea extends Component {
  constructor(prop) {
    super(prop);
    this.state = {
      question: "What is your focus area in computer science?"
      // later on may want to add componentDidMount() to read focus areas from the DB
    };
  }

  render() {
    return (
      <div class="center">
        <h1
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center"
          }}
        >
          {this.state.question}
        </h1>
        <div
          style={{
            marginLeft: "auto",
            display: "flex",
            justifyContent: "center"
          }}
        >
        <Link to="/r">
          <button class="button1" type="button">
            Robotics
          </button>
        </Link>
        <Link to="/bd">
          <button class="button4" type="button">
            Big Data
          </button>
        </Link>
        <Link to="/is">
          <button class="button5" type="button">
            Information Security
          </button>
        </Link>
        <Link to="/cb">
          <button class="button3" type="button">
            Computational Biology
          </button>
        </Link>
        <Link to="/nlp">
          <button class="button2" type="button">
            Natural Language Processing
          </button>
        </Link>
        </div>
      </div>
    );
  }
}


class NLP extends Component {
  constructor(prop) {
    super(prop);
    this.state = {
      apiResponse: "",
      schedule: []
    };
  }

  callAPI() {
    console.log("fetching from api");
    fetch("http://localhost:5000/api/nlp/courses")
      .then(res => res.json())
      .then(res => this.setState({ schedule: res }))
      .catch(err => err);
  }

  componentDidMount() {
    this.callAPI();
  }

  render() {
    const listItems = this.state.schedule.map(d => {
      return (
        <li>
          {d.CourseNumber}
          {": "}
          {d.CourseTitle}
          {" Credit:"}
          {d.Credits}
          {" Instructor:"}
          {d.Instructor}
        </li>
      );
    });
    return (
      <div class="center1">
        <h1
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center"
          }}
        >
          Welcome to Natural Language Processing!
        </h1>
        <div>{listItems}</div>
      </div>
    );
  }
}

