import React, { Component } from "react";
import logo from "./logo.svg";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    return (
      <Router>
        <Switch>
          <Route exact path="/">
            <CoursesTaken />
          </Route>
          <Route exact path="/focus_area">
            <FocusArea />
          </Route>
          <Route exact path="/nlp">
            <NLP />
          </Route>
          <Route exact path="/r">
            <ROB />
          </Route>
          <Route exact path="/bd">
            <BD />
          </Route>
          <Route exact path="/is">
            <IS />
          </Route>
          <Route exact path="/cb">
            <CB />
          </Route>
        </Switch>
      </Router>
    );
  }
}


class CoursesTaken extends Component {
  constructor(prop) {
    super(prop);
    this.state = {
      question: "What courses have you taken?",
      value: "", // for display in the input box
      allCourses: ['hello', 'bye'], 
      myCourses: []
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  callAPI() {
    console.log("fetching from api");
    fetch("http://localhost:5000/api/nlp/courses") // to be changed
      .then(res => res.json())
      .then(res => this.setState({ allCourses: res }))
      .catch(err => err);
  }

  componentDidMount() {
    this.callAPI();
  }

  handleChange(event) {
    this.setState({value: event.target.value}); // prints input out by setting values to the changes
  }

  handleSubmit(event) {
    let name = document.querySelector("#courseName");
    if (!this.state.allCourses.includes(name.value)) {
      alert(name.value + ' is invalid!');
      console.log(name.value);
    } else {
      alert('Successfully added ' + this.state.value + '!');
      this.setState(state => {
        const myCourses = state.myCourses.concat(state.value);
        return {
          value:"",
          myCourses,
        };
      });
      event.preventDefault();
    }

  }

  render() {
      return (
        <div class="center">
          <h1
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}
          >
            {this.state.question}
          </h1>
          <form onSubmit={this.handleSubmit} style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}>
            <input type="text" placeholder="Course Name" id="courseName" value={this.state.value} size="60" onChange={this.handleChange}/>
            <Link to="/focus_area">
              <button class="button0" type="button" align="right">
                That's it
              </button>
          </Link>
          </form>
          <div>{this.state.myCourses.map(data => (<li>{data}</li>))}</div>

        </div>
    );
  }
}


