import React, { Component } from "react";
class NLP extends Component {
    constructor(prop) {
      super(prop);
      this.state = {
        schedule1: [],
        schedule2: [],
        schedule3: []
      };
    }

    callAPI() {
      console.log("fetching from api");
      fetch("http://localhost:5000/api/nlp/courses")
        .then(res => res.json())
        .then(res => this.setState({ 
          schedule1: res.slice(0, 3),
          schedule2: res.slice(3, 6),
          schedule3: res.slice(6, 9)}))
        .catch(err => err);
    }

    componentDidMount() {
      this.callAPI();
    }

    render() {
      const list1 = this.state.schedule1.map(d => {
        return (
          <li>
            {d.CourseNumber}
            {": "}
            {d.CourseTitle}
            {" Credit:"}
            {d.Credits}
            {" Instructor:"}
            {d.Instructor}
          </li>
        );
      });
      const list2 = this.state.schedule2.map(d => {
        return (
          <li>
            {d.CourseNumber}
            {": "}
            {d.CourseTitle}
            {" Credit:"}
            {d.Credits}
            {" Instructor:"}
            {d.Instructor}
          </li>
        );
      });
      const list3 = this.state.schedule3.map(d => {
        return (
          <li>
            {d.CourseNumber}
            {": "}
            {d.CourseTitle}
            {" Credit:"}
            {d.Credits}
            {" Instructor:"}
            {d.Instructor}
          </li>
        );
      });
      return (
        <div>
          <h1
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}
          >
            Welcome to Natural Language Processing!
          </h1>
          <div class="text2">
            <h1>Recommended Schedule #1</h1>
          </div>
          <div class="box2">{list1}</div>
          <div class="text1">
            <h1>Recommended Schedule #2</h1>
          </div>
          <div class="box1">{list2}</div>
          <div class="text3">
            <h1>Recommended Schedule #3</h1>
          </div>
          <div class="box3">{list3}</div>
        </div>
      );
    }
  }

  export default NLP;
