import React, { Component } from "react";
import logo from "./logo.svg";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {};

    // this.handleSubmit = this.handleSubmit.bind(this);
    // this.handleChange = this.handleChange.bind(this);
    // this.handleClick = this.handleClick.bind(this);
  }

  // handleChange(event) {
  //   this.setState({ number: event.target.value });
  // }

  // handleSubmit(event) {
  //   fetch("http://localhost:5000/api/courses/" + this.state.number)
  //     .then(res => res.json())
  //     .then(data => this.setState({ apiResponse: data.name }));
  //   event.preventDefault();
  // }



  render() {
    return (
      <Router>
        <Switch>
          <Route exact path="/">
            <CoursesTaken />
          </Route>
          <Route exact path="/focus_area">
            <FocusArea />
          </Route>
          <Route exact path="/nlp">
            <NLP />
          </Route>
          <Route exact path="/r">
            <ROB />
          </Route>
          <Route exact path="/bd">
            <BD />
          </Route>
          <Route exact path="/is">
            <IS />
          </Route>
          <Route exact path="/cb">
            <CB />
          </Route>
        </Switch>
      </Router>
    );
  }
}


class CoursesTaken extends Component {
  constructor(prop) {
    super(prop);
    this.state = {
      question: "What courses have you taken?",
      value: "",
      allCourses: [], 
      myCourses: ["helo", "jdahsj"]
    };
  }

  callAPI() {
    console.log("fetching from api");
    fetch("http://localhost:5000/api/nlp/courses") // to be changed
      .then(res => res.json())
      .then(res => this.setState({ allCourses: res }))
      .catch(err => err);
  }

  componentDidMount() {
    this.callAPI();
  }

  handleInputChange(event) {

  }

  handleSubmit(event) {
    alert(this.state.value + 'added/n ');
    event.preventDefault();
  }

  render() {
    const listItems = this.state.myCourses.map(d => {
      return (
        <li>
          {d}
        </li>
      );
    });
      return (
        <div>
          <h1
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}
          >
            {this.state.question}
          </h1>
          <form onSubmit={this.handleSubmit} style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}>
            <input type="text" value={this.state.value} size="60" onChange={this.handleInputChange}/>
            <Link to="/focus_area">
              <button class="button0" type="button" align="right">
                That's it
              </button>
          </Link>
          </form>
          <div>{listItems}</div>
        </div>
    );
  }
}

