  }
}

class BD extends Component {
  constructor(prop) {
    super(prop);
    this.state = {
      apiResponse: "",
      schedule: []
    };
  }

  callAPI() {
    fetch("http://localhost:5000/api/bd/courses")
      .then(res => res.json())
      .then(res => this.setState({ schedule: res }))
      .catch(err => err);
  }

  componentDidMount() {
    this.callAPI();
  }

  render() {
    const listItems = this.state.schedule.map(d => {
      return (
        <li>
          {d.CourseNumber}
          {": "}
          {d.CourseTitle}
          {" Credit:"}
          {d.Credits}
          {" Instructor:"}
          {d.Instructor}
        </li>
      );
    });
    return (
      <div>
        <h1
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center"
          }}
        >
          Welcome to Big Data!
        </h1>
        <div>{listItems}</div>
      </div>
    );
  }
}

export default App;
