import React, { Component } from "react";
import logo from "./logo.svg";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import "./App.css";

class App extends Component {
    constructor(props) {
      super(props);
      this.state = {};
    }

    render() {
      return (
        <Router>
          <Switch>
            <Route exact path="/">
              <CoursesTaken />
            </Route>
            <Route exact path="/focus_area">
              <FocusArea />
            </Route>
            <Route exact path="/nlp">
              <NLP />
            </Route>
            <Route exact path="/r">
              <ROB />
            </Route>
            <Route exact path="/bd">
              <BD />
            </Route>
            <Route exact path="/is">
              <IS />
            </Route>
            <Route exact path="/cb">
              <CB />
            </Route>
          </Switch>
        </Router>
      );
    }
  }


class CoursesTaken extends Component {
  constructor(prop) {
    super(prop);
    this.state = {
      question: "What courses have you taken?",
      value: "", // for display in the input box
      allCourses: ["hello", "bye"], 
      myCourses: []
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  callAPI() {
    console.log("fetching from api");
    fetch("http://localhost:5000/api/courses") // to be changed
      .then(res => res.json())
      .then(res => this.setState({ allCourses: res }))
      .catch(err => err);
  }

  sendAPI(data) {
    console.log("posting to api");
    fetch('http://localhost:5000/api/user_info', {
      method: 'POST',
      body: JSON.stringify(data)
    }).then(res => res.json())
    .then(data => console.log("Success", data))
    .catch(err => console.log("Error:", err));
  }

  // showData(data) {
  //   console.log(JSON.stringify(data))
  // }

  componentDidMount() {
    this.callAPI();
  }

  handleChange(event) {
    this.setState({value: event.target.value}); // prints input out by setting values to the changes
  }

  handleSubmit(event) {
    alert('Successfully added ' + this.state.value + '!');
    this.setState(state => {
      const myCourses = state.myCourses.concat(state.value);
      return {
        value:"",
        myCourses,
      };
    });
    event.preventDefault();
  }


  render() {
      const optionList = this.state.allCourses.map(it => 
              <option>
                {it.CourseTitle}
              </option>
              );
        console.log("OPTION:",optionList)

      return (
        <div class="center">
          <h1
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}
          >
            {this.state.question}
          </h1>
          <form onSubmit={this.handleSubmit} style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}>
            <label> Choose your course from the list: <i class="iconfont">&#xe600;</i>
            <div>
            <select id="courses" value={this.state.value} onChange = {this.handleChange}>
            <option value=""> Select Your Courses </option>
  {optionList}
</select> 
</div>
<input type="submit" value="I've taken it!" />
</label>
          </form>
          <div>{this.state.myCourses.map(data => (<li>{data}</li>))}</div>

          {/* <label>
    Choose a course from this list:
   <input list="courses" id="prereq" autoComplete = "on"/>  
</label>    */}
         
<Link to="/focus_area">
              <button onClick = {() => this.sendAPI(this.state.myCourses)} class="button0" type="button" align="right">
                That's it
              </button>
          </Link>
        </div>

    );
  }
}



